using json = nlohmann::json;  

static size_t write_cb(void *ptr, size_t size, size_t nmemb, void *userdata) {
    auto *out = static_cast<std::string *>(userdata);
    out->append(static_cast<char *>(ptr), size * nmemb);
    return size * nmemb;
}

